import React, { useState } from "react";

function UseStateWithObjects() {
  const [name, setName] = useState({ firstName: "", lastName: "" });

  return (
    <div>
      <form>
        <input
          type="text"
          value={name.firstName}
          onChange={(e) =>
            setName({
              ...name,
              firstName: e.target.value,
            })
          }
        />
        <input
          type="text"
          value={name.lastName}
          onChange={(e) =>
            setName({
              ...name,
              lastName: e.target.value,
            })
          }
        />
        <h2>{JSON.stringify(name)}</h2>
      </form>
    </div>
  );
}

export default UseStateWithObjects;

/*
useState hook:
an object passed to the setter function
1. overwrites the old one, so always spread old values before setting new ones
